import React, { useEffect, useState } from "react";
import { useContext } from "react";
import { ComponentContext } from "../../ComponentContext/ComponentContext";
import "./Main.css";

const Main = ({ component }) => {
  const { state, sender } = useContext(ComponentContext);
  const { contract } = state;
  const [ProposalList, setProposalList] = useState([]);
  const [InvestorList, setInvestorList] = useState([]);

  useEffect(() => {
    try {
      const setLists = async () => {
        const proposalList = await contract.methods.ProposalList().call();
        setProposalList(proposalList);
        console.log(proposalList);
        const investorList = await contract.methods.InvestorList().call();
        setInvestorList(investorList);
        console.log(investorList);
      };
      contract && setLists();
    } catch (error) {
      console.error(error);
    }
  }, [state, sender]);

  return (
    <>
      <section className="main-section">
        {component}
        <div id="proposalList" className="lists">
          <h3>Proposal's List</h3>
          <div>
            {ProposalList.map((proposal) => (
              <div key={proposal.id}>
                <p>ID: {proposal.id}</p>
                <p>Description: {proposal.description}</p>
                <p>Recipient: {proposal.recepient}</p>
                <p>Votes: {proposal.votes}</p>
                <p>End Date: {proposal.end}</p>
                <p>Executed: {proposal.isExecuted.toString()}</p>
              </div>
            ))}
          </div>
        </div>
        <div id="investorList" className="lists">
          <h3>Investor's List</h3>
          <div>
            {InvestorList.map((investor) => (
              <p key={investor}>{investor}</p>
            ))}
          </div>
        </div>
      </section>
      <section className="primary-section"></section>
    </>
  );
};

export default Main;
