import React, { useContext, useRef } from "react";
import "./CreateProposal.css";
import { ComponentContext } from "../../../ComponentContext/ComponentContext";
const CreateProposal = () => {
  const { state } = useContext(ComponentContext);
  const { contract } = state;
  console.log(contract);
  const descriptionInput = useRef(null);
  const amountInput = useRef(null);
  const addressInput = useRef(null);

  const createProposal = async (e) => {
    e.preventDefault();
    const description = descriptionInput.current.value;
    const amount = amountInput.current.value;
    const address = addressInput.current.value;
    await contract.createProposal(description, amount, address).call;
  };

  return (
    <>
      <div className="createProp-box">
        <h2>Create Your Proposal</h2>
        <form action="">
          <input
            ref={descriptionInput}
            type="text"
            name=""
            id=""
            placeholder="Proposal Descripton"
          />
          <input
            ref={amountInput}
            type="text"
            name=""
            id=""
            placeholder="Amount in (Wei)"
          />
          <input
            ref={addressInput}
            type="text"
            name=""
            id=""
            placeholder="Recipient Address"
          />
          <button type="submit" onSubmit={createProposal}>
            Create
          </button>
        </form>
      </div>
    </>
  );
};

export default CreateProposal;
