import React, { useEffect, useState } from "react";
import { useContext } from "react";
import { ComponentContext } from "../../ComponentContext/ComponentContext";
import "./Main.css";
const Main = ({ component }) => {
  const { state, sender } = useContext(ComponentContext);
  const { contract } = state;
  const [list, setList] = useState(null);
  useEffect(() => {
    try {
      const call = async () => {
        const list = await contract.methods.ProposalList().call();
        setList(list);
      };
      contract && call();
    } catch (error) {
      console.error(error);
    }
  }, [state, sender]);

  return (
    <>
      <section className="main-section">
        {component}
        <div id="investorList" className="lists">
          <h3>Investor's List</h3>
          <p>{list.id}</p>
          <div className="list-box"></div>
        </div>
        <div id="proposalList" className="lists">
          <h3>Proposal's List</h3>
        </div>
      </section>
      <section className="primary-section"></section>
    </>
  );
};

export default Main;
