import "./App.css";
import Navbar from "./components/NavBar/NavBar";
import Dashboard from "./components/Dashboard/Dashboard";
import HeroSection from "./components/HeroSection/HeroSection";
import { useEffect, useState } from "react";
import { Web3 } from "web3";
import DAO from "./contractABI/DAO.json";
const App = () => {
  // setting contract and state
  const [state, setState] = useState({ web3: null, contract: null });

  // const [account, setAccount] = useState("");
  // const [balance, setBalance] = useState(0);
  useEffect(() => {
    const init = async () => {
      const provider = new Web3.providers.HttpProvider("HTTP://127.0.0.1:7545");
      const web3 = new Web3(provider);

      const networkId = await web3.eth.net.getId();

      const deployedNetwork = DAO.networks[networkId];

      const contract = new web3.eth.Contract(DAO.abi, deployedNetwork.address);
      setState({ web3: web3, contract: contract });
    };
    init();
  });

  const select = useRef("");

  useEffect(() => {
    // abstract web3 from state
    const { web3 } = state;
    console.log(web3);
    const allAccounts = async () => {
      // getting all accounts from web3
      const options = await web3.eth.getAccounts();

      for (let i = 0; i < options.length; i++) {
        // Change made here
        let option = options[i];
        let element = document.createElement("option");
        element.textContent = option;
        element.value = option;
        select.current.appendChild(element);
      }
    };
    web3 && allAccounts();
  });

  return (
    <>
      <section className="primary-section">
        <Navbar />
        <Dashboard />
        <HeroSection state={state} />
      </section>
    </>
  );
};
export default App;
