import "./App.css";
import Navbar from "./components/NavBar/NavBar";
import Dashboard from "./components/Dashboard/Dashboard";
import HeroSection from "./components/HeroSection/HeroSection";
import { useEffect, useState } from "react";
import Web3 from "web3"; // Note: Web3 should not be destructured
import DAO from "./contractABI/DAO.json";

const App = () => {
  const [state, setState] = useState({ web3: null, contract: null });

  useEffect(() => {
    const init = async () => {
      const provider = new Web3.providers.HttpProvider("HTTP://127.0.0.1:7545");
      const web3 = new Web3(provider);
      const networkId = await web3.eth.net.getId();
      console.log("Network ID:", networkId);

      const deployedNetwork = DAO.networks[networkId];
      console.log("Deployed Network:", deployedNetwork);

      const contract = new web3.eth.Contract(DAO.abi, deployedNetwork.address);

      console.log(contract);
      setState({ web3: web3, contract: contract });
    };

    init();
  }, []);

  return (
    <section className="primary-section">
      <Navbar />
      <Dashboard />
      <HeroSection />
    </section>
  );
};

export default App;
