import React, { useContext, useRef, useState } from "react";
import { ComponentContext } from "../../../ComponentContext/ComponentContext";

const ContributeAmount = () => {
  const { state, sender } = useContext(ComponentContext);
  const { contract, web3 } = state;
  const amountInput = useRef(0);
  const Contribute = async (e) => {
    try {
      e.preventDefault();
      const amount = web3.utilis.toWei(amountInput.current.value);
      await contract.methods
        .contribution()
        .send({ from: sender, value: amount, gas: 48000 });
      console.log("Transaction Successful");
    } catch (error) {
      console.error("Error is : ", error);
    }
  };

  return (
    <>
      <div className="createProp-box">
        <h2>Contribute Amount</h2>
        <form action="">
          <input
            type="text"
            name=""
            id=""
            ref={amountInput}
            placeholder="Enter Contribution Amount in Wei"
          />
          <button type="submit" onClick={Contribute}>
            Contribute
          </button>
        </form>
      </div>
    </>
  );
};

export default ContributeAmount;
