import React, { useContext, useRef, useState } from "react";
import { ComponentContext } from "../../../ComponentContext/ComponentContext";

const TransferShares = () => {
  const { state } = useContext(ComponentContext);
  const { contract, web3 } = state;
  const [Input, setInput] = useState({
    address: "",
    amount: "",
  });

  const getValues = (e) => {
    const { name, value } = e.target;
    console.log(name, value);

    setInput((prevInput)=>{
      
      setInput({ ...setInput, [name]: value });
    })
    console.log("Input ", Input);
  };
  const transaferShares = async (e) => {
    if (!Input.amount || !Input.address) {
      console.log("Field must be filled");
      return;
    }

    try {
      e.preventDefault();
      const { amount, address } = Input;

      const toWei = await web3.utils.toWei(amount);
      console.log("Amount to wei : ", toWei);
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <>
      <div className="createProp-box">
        <h2>Transfer Shares</h2>
        <form action="">
          <input
            type="text"
            placeholder="Enter Amount in Wei"
            name="amount"
            value={Input.amount}
            onChange={getValues}
          />
          <input
            type="text"
            placeholder="Enter Recipient Address "
            name="address"
            value={Input.address}
            onChange={getValues}
          />
          <button type="submit" onClick={transaferShares}>
            Transfer
          </button>
        </form>
      </div>
    </>
  );
};

export default TransferShares;
