import React, { useContext, useRef } from "react";
import "./CreateProposal.css";
import { ComponentContext } from "../../../ComponentContext/ComponentContext";

const CreateProposal = () => {
  const { state, sender, web3 } = useContext(ComponentContext);
  const { contract } = state;

  const descriptionInput = useRef(null);
  const amountInput = useRef(null);
  const addressInput = useRef(null);

  const initiateProposal = async (e) => {
    try {
      e.preventDefault();
      const description = descriptionInput.current.value.trim();
      const amount = parseInt(amountInput.current.value);
      const recipientAddress = addressInput.current.value.toLowerCase();

      if (!description || !amount || !recipientAddress) {
        throw new Error("Please fill out all fields");
      }

      await contract.methods
        .createProposal(description, amount, recipientAddress)
        .send({
          from: sender,
          gas: 480000,
          gasPrice: web3.utils.toWei("20", "gwei"),
        })
        .on("transactionHash", (hash) => {
          console.log(`Transaction sent: ${hash}`);
        })
        .on("confirmation", (receipt) => {
          if (receipt.status === false) {
            handleRevertError(web3, receipt.transactionHash);
          }
        });

      console.log("Proposal Created");
    } catch (error) {
      // console.error("An error occurred:", error.message);
      // // Display the error to the user
      // alert(`Error: ${error.message}`);
    }
  };

  function handleRevertError(web3, trxHash) {
    web3.eth.getTransaction(trxHash).then((trx) => {
      if (trx.receipt?.status === false && trx.logs?.length > 0) {
        const errorReason = trx.logs[0].data.toString().slice(10);
        console.error(`Transaction reverted: ${errorReason}`);
        alert(`Transaction reverted: ${errorReason}`);
      }
    });
  }

  return (
    <>
      <div className="createProp-box">
        <h2>Create Your Proposal</h2>
        <form onSubmit={initiateProposal}>
          <input
            ref={descriptionInput}
            type="text"
            name="description"
            id="description"
            placeholder="Proposal Description"
            required
          />
          <input
            ref={amountInput}
            type="number"
            name="amount"
            id="amount"
            placeholder="Amount in Wei"
            required
          />
          <input
            ref={addressInput}
            type="text"
            name="recipientAddress"
            id="recipientAddress"
            placeholder="Recipient Address"
            required
          />
          <button type="submit">Create</button>
        </form>
      </div>
    </>
  );
};

export default CreateProposal;
