import React, { useEffect, useRef, useState } from "react";
import Main from "../Main/Main";
import "./HeroSection.css";

const HeroSection = ({ state }) => {
  const select = useRef("");
  const [ConAccount, setContAccount] = useState("Not Connected");
  useEffect(() => {
    // abstract web3 from state
    const { web3 } = state;
    console.log(web3);
    const allAccounts = async () => {
      // getting all accounts from web3
      const options = await web3.eth.getAccounts();

      for (let i = 0; i < options.length; i++) {
        // Change made here
        let option = options[i];
        let element = document.createElement("option");
        element.textContent = option;
        element.value = option;
        select.current.appendChild(element);
      }
    };
    web3 && allAccounts();
  }, [state]);

  return (
    <>
      <section className="hero-section">
        <header>
          <h2>Connected Account : (Connected Account)</h2>
          <h3> Available Funds : (Funds)</h3>
          <select name="" id="options" ref={select}>
            <option value="">Select Your account</option>
          </select>
        </header>
        <Main />
      </section>
    </>
  );
};

export default HeroSection;
