import React from "react";
import { useRef, useContext } from "react";
import { ComponentContext } from "../../../ComponentContext/ComponentContext";
const Vote = () => {
  const { state, sender } = useContext(ComponentContext);
  const { contract } = state;
  const inputProposalId = useRef(null);
  const voteProposal = async (e) => {
    try {
      e.preventDefault();
      const proposalId = parseInt(inputProposalId.current.value);
      const gasEstimate = await contract.methods.contribution().estimateGas({
        from: sender,
        value: amount,
      });
      await contract.methods
        .voteProposal(proposalId)
        .send({ from: sender, gas: 48000 });
    } catch (error) {
      alert(error);
    }
  };
  return (
    <>
      <div className="createProp-box">
        <h2>Cast Vote</h2>
        <form action="">
          <input
            type="text"
            ref={inputProposalId}
            name=""
            id=""
            placeholder="Enter Proposal ID"
          />
          <button type="submit" id="vote-btn" onClick={voteProposal}>
            Vote
          </button>
        </form>
      </div>
    </>
  );
};

export default Vote;
